import React, { Component } from "react";
import { fire } from "./config/fbConfig";
import "./App.css";
import image from "./static/images/abs.svg";
import gif from "./static/images/ring2.gif";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      subscribers: [],
      loading: false
    };

    this.itemsRef = fire.ref("subscriber");
  }

  componentDidMount() {
    this.setState({ loading: true });
    this.itemsRef.on("value", data => {
      var subscriber = data.val() || {};
      var keys = Object.keys(subscriber);
      const subscribers = keys.map(key => ({ key, ...subscriber[key] }));
      this.setState({ subscribers, loading: false });
    });
  }

  handleSubmit = e => {
    e.preventDefault();
    if (this.refs.phone_number.value.length !== 10) {
      alert("Invalid Phone Number");
    } else {
      this.itemsRef.push({
        username: this.refs.username.value,
        phone_number: this.refs.phone_number.value
      });

      this.refs.username.value = "";
      this.refs.phone_number.value = "";
    }
  };

  deleteSubscriber = id => {
    this.itemsRef.update({
      [id.key]: null
    });
  };

  render() {
    const { length: count } = this.state.subscribers;
    return (
      <React.Fragment>
	<div className="nav">Phone Directory</div>
        <div className="container">

          <div>
            <img className="logo" src={image} alt="logo" />
            <h1> Mini Phone Directory </h1>
            <form onSubmit={this.handleSubmit}>
              <div className="form-group" id="spc-1">
                <div className="input-1">
                  <i className="fa fa-user" />
                  <input
                    type="text"
                    className="form-control"
                    ref="username"
                    placeholder="Your Name"
                    required
                  />
                </div>
              </div>
              <div className="form-group">
                <div className="input-1">
                  <i className="fa fa-phone" />
                  <input
                    type="number"
                    className="form-control"
                    minLength="10"
                    maxLength="10"
                    size="10"
                    ref="phone_number"
                    placeholder="Your Phone Number"
                    required
                  />
                </div>
              </div>
              <button type="submit" className="btn btn-primary">
                Create New Contact
              </button>
            </form>
          </div>
        </div>
        {this.state.loading ? (
          <div className="gif">
            <img src={gif} alt="gif" />
          </div>
        ) : count === 0 ? (
          <p className="gif">There are no subscriber in Database</p>
        ) : (
          <div className="list">
            <table className="table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Mobile Number</th>
                  <th />
                </tr>
              </thead>
              <tbody>
                {this.state.subscribers.map(subscriber => (
                  <tr key={subscriber.key}>
                    <td>{subscriber.username}</td>
                    <td>{subscriber.phone_number}</td>
                    <td>
                      <button
                        className="unsubscribe--button"
                        onClick={() => this.deleteSubscriber(subscriber)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </React.Fragment>
    );
  }
}

export default App;
